package com.megacreep.LeetCode;

import java.util.HashMap;
import java.util.Map;

/**
 * Created by megacreep on 2014/9/22.
 */
public class DistinctSubSequences {
    Map<String, Map<String, Integer>> countMap = new HashMap<String, Map<String, Integer>>();

    public int numDistinct(String S, String T) {
        if (S == null || T == null) {
            return 0;
        }

        int[][] num = new int[S.length() + 1][T.length() + 1];

        for (int i = 0; i < num.length; i++) {
            num[i][0] = 1;
        }

        for (int i = 1; i <= S.length(); i++) {
            for (int j = 1; j <= T.length() && j <= i; j++) {
                num[i][j] = num[i - 1][j];
                if (S.charAt(i - 1) == T.charAt(j - 1)) {
                    num[i][j] += num[i - 1][j - 1];
                }
            }
        }

        return num[S.length()][T.length()];
    }

    public int numDistinctUgly(String S, String T) {
        if (T == null || S.length() < T.length()) {
            return 0;
        }

        if (T.isEmpty()) {
            return 1;
        }

        int start = S.indexOf(T.charAt(0));
        int end = S.lastIndexOf(T.charAt(T.length() - 1));
        if (start == -1 || end == -1) {
            return 0;
        }
        S = S.substring(start, end + 1);

        if (countMap.containsKey(S) && countMap.get(S).containsKey(T)) {
            return countMap.get(S).get(T);
        }



        int count = 0;
        int index = 0;
        while ((index = S.indexOf(T.charAt(0), index)) != -1) {
            count += numDistinct(S.substring(index + 1), T.substring(1));
            index++;
        }


        if (!countMap.containsKey(S)) {
            countMap.put(S, new HashMap<String, Integer>());
        }
        Map<String, Integer> map = countMap.get(S);
        map.put(T, count);
        return count;
    }

    public static void main(String[] args) {
        String S = "";
        String T = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
        long start = System.currentTimeMillis();
        System.out.println("Count: " + new DistinctSubSequences().numDistinct(S, T));
        System.out.println("time: " + (System.currentTimeMillis() - start));
    }
}
